import React, { useState } from 'react';
import './form.css';

function Form(){
const [name,setName]=useState("")
const [newname,newsetName]=useState("")

const [sname,ssetName]=useState("")
const [snewname,snewsetName]=useState("")


    function add(event){
        var name1=event.target.value;
        setName(name1)
    }

    function add1(event){
        var name2=event.target.value;
        ssetName(name2) 
    }

    function listItem(e){
        e.preventDefault();
        newsetName(name)
        snewsetName(sname)
        console.log(name)

    }

    return (
    <>
    <h1 className="heading">Fill Form</h1>
    <form className="form_head" onSubmit={listItem}>
    <input type="text"  placeholder="Enter Task" 
    value={name} 
    className="input" 
    onChange={add}
    />
    <input type="text"  placeholder="Enter Task" 
    value={sname} 
    className="input" 
    onChange={add1}
    />
     <li>{newname}</li>
     <li>{snewname}</li>

    <button type="submit" >Add Task</button>
    </form>
    </>

    );
}


export default Form;
